        <h1 class=\"text-3xl font-bold mb-6\">Edit User</h1>

        {{ form_start(form, {'attr': {'class': 'bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4'}}) }}
            {{ form_row(form.nom) }}
            {{ form_row(form.prenom) }}
            {{ form_row(form.login) }}
            {{ form_row(form.password, {'attr': {'placeholder': 'Leave blank to keep current password'}}) }}
            {{ form_row(form.isActive) }}

            <div class=\"flex items-center justify-between mt-4\">
                <button class=\"bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline\" type=\"submit\">
                    Update
                </button>
                <a href=\"{{ path('app_user_index') }}\" class=\"inline-block align-baseline font-bold text-sm text-blue-500 hover:text-blue-800\">
